CREATE DATABASE factumockup;
USE factumockup;
CREATE TABLE formaspago (id INT NOT NULL AUTO_INCREMENT, company INT NOT NULL, nombrepago VARCHAR(45) , diff INT , diapago INT , PRIMARY KEY AUTO_INCREMENT (id));
CREATE TABLE works (id INT NOT NULL AUTO_INCREMENT, company INT NOT NULL, fecha DATE NOT NULL, numero VARCHAR(15) NOT NULL, cliente INT NOT NULL, texto VARCHAR(200) NOT NULL, cantidad FLOAT NOT NULL, importe DECIMAL(7,2) NOT NULL, base DECIMAL (7,2) NOT NULL, iva DECIMAL (4,2) NOT NULL, total DECIMAL (8,2) NOT NULL, factura VARCHAR(15), PRIMARY KEY(id) );
CREATE TABLE customer (id INT NOT NULL AUTO_INCREMENT, company INT NOT NULL, nombre VARCHAR(50) NOT NULL, direccion VARCHAR(50) NOT NULL, codpostal VARCHAR(5) NOT NULL, localidad VARCHAR(50) NOT NULL, nif VARCHAR(9) NOT NULL, formapago INT NOT NULL, PRIMARY KEY(id) );
CREATE TABLE invoices (id INT NOT NULL AUTO_INCREMENT, company INT NOT NULL, fecha DATE NOT NULL, numero VARCHAR(15) NOT NULL, destinatario INT NOT NULL, albaranes INT NOT NULL, baseimponible1 DECIMAL(7,2) NOT NULL, cuotaiva1 DECIMAL (7,2) NOT NULL, baseimponible2 DECIMAL(7,2) NOT NULL, cuotaiva2 DECIMAL (7,2) NOT NULL, baseimponible3 DECIMAL(7,2) NOT NULL, cuotaiva3 DECIMAL (7,2) NOT NULL, total DECIMAL (8,2) NOT NULL, formapago INT NOT NULL, vencimiento VARCHAR(50) NOT NULL, PRIMARY KEY(id) );
CREATE TABLE invoices_works(id INT NOT NULL AUTO_INCREMENT, company INT NOT NULL, num_invoice VARCHAR(15) NOT NULL, id_works INT NOT NULL, PRIMARY KEY(id));